// PRIVATE :: SHA1 :: basic operation 0 for the sha1 algorithm
var sha1_ft = function(t, b, c, d) {
	if(t < 20) {
		return (b & c) | ((~b) & d);
	} //end if
	if(t < 40) {
		return b ^ c ^ d;
	} //end if
	if(t < 60) {
		return (b & c) | (b & d) | (c & d);
	} //end if
	return b ^ c ^ d;
} //END FUNCTION

