// PRIVATE :: SHA512
var sha512_rot_r = function(x, n) {
	//--
	if(n <= 32) {
		return new sha512_int_64(
			(x.highOrder >>> n) | (x.lowOrder << (32 - n)),
			(x.lowOrder >>> n) | (x.highOrder << (32 - n))
		);
	} else {
		return new sha512_int_64(
			(x.lowOrder >>> n) | (x.highOrder << (32 - n)),
			(x.highOrder >>> n) | (x.lowOrder << (32 - n))
		);
	} //end if else
	//--
} //END FUNCTION

// PRIVATE :: SHA512
