// syntax/tpl/tex.js
// HighlightJs: v.9.13.1

hljs.registerLanguage('tex',
function(hljs) {
	var COMMAND = {
		className: 'tag',
		begin: /\\/,
		relevance: 0,
		contains: [
			{
				className: 'name',
				variants: [
					{begin: /[a-zA-Zа-яА-я]+[*]?/},
					{begin: /[^a-zA-Zа-яА-я0-9]/}
				],
				starts: {
					endsWithParent: true,
					relevance: 0,
					contains: [
						{
							className: 'string', // because it looks like attributes in HTML tags
							variants: [
								{begin: /\[/, end: /\]/},
								{begin: /\{/, end: /\}/}
							]
						},
						{
							begin: /\s*=\s*/, endsWithParent: true,
							relevance: 0,
							contains: [
								{
									className: 'number',
									begin: /-?\d*\.?\d+(pt|pc|mm|cm|in|dd|cc|ex|em)?/
								}
							]
						}
					]
				}
			}
		]
	};

	return {
		contains: [
			COMMAND,
			{
				className: 'formula',
				contains: [COMMAND],
				relevance: 0,
				variants: [
					{begin: /\$\$/, end: /\$\$/},
					{begin: /\$/, end: /\$/}
				]
			},
			hljs.COMMENT(
				'%',
				'$',
				{
					relevance: 0
				}
			)
		]
	};
}
);

